function convertLinks(){let e=document.getElementById("driveLinks").value.split(", "),t=document.getElementById("width").value,n=document.getElementById("height").value,l=document.getElementById("convertedUrls");l.innerHTML="",e.forEach(e=>{let c=e.match(/\/file\/d\/(.*?)\//)[1],r=`https://drive.google.com/thumbnail?id=${c}&sz=w${t}-h${n}`,i=document.createElement("span");i.textContent=r,l.appendChild(i),l.appendChild(document.createElement("br"))})}function copyUrls(){let e=document.getElementById("convertedUrls"),t=e.innerText;navigator.clipboard.writeText(t).then(()=>{console.log("Text copied successfully!"),alert("Text copied successfully!")}).catch(e=>{console.error("Error copying text: ",e),alert("Error copying text!")})}document.getElementById("convertButton").addEventListener("click",function(){convertLinks()}),document.getElementById("copyButton").addEventListener("click",function(){copyUrls()});
